import '../styles.css';
import React from 'react';


function Square(){
    const [value, setValue] = React.useState(null);

    const handleClick = () => {
        console.log("Clicked!");
        if (!value) {
            setValue("X");
        } else if (value == "X") {
            setValue("O");
        }
    }

    return (
        <button className="square-button" onClick={handleClick}>
                {value}
        </button>
    );
}

export default Square;
